            str = str..toJson(v[1], refs)
            for i = 2, #v, 1 do
                str = str..", "..toJson(v[i], refs)
            end
            str = str.."]"
        else
            str = "{"
            for key, val in pairs(v) do
                str = str.."'"..key.."'"..kvSep..toJson(val, refs)..","
            end
            if(string.len(str) > 4) then
